mapper.select:
select s_idx, s_name, s_url, s_img, s_cafeUrl, ( select COUNT(*) from ssafy_trot.`Following` f where f.s_idx = s.s_idx ) as f_cnt
,( select COUNT(*) from ssafy_trot.`Following` f where 1=1 and f.s_idx = #{b_idx} and f.u_email = #{u_email} )as f_flag
from Singer s where s_idx =#{b_idx};

 	select s_name
	from Singer 
	where s_idx =#{s_idx};

mapper.delete:
delete 	from Good
where 	1=1
and		b_type = #{b_type} 
and		b_idx = #{b_idx} 
and		u_email = #{u_email};
