mapper.update:
UPDATE	UserInfo 
SET 	u_hasVote = 1
WHERE 	u_email = #{u_email};

mapper.select:
select	u_hasVote 
from	UserInfo
where	u_email = #{u_email};

SELECT 	s_name
from	Singer
where	s_idx = #{s_idx};

SELECT 	COUNT(s_idx) 
FROM 	Vote
where	s_idx = #{s_idx};

SELECT 	COUNT(s_name) 
FROM 	VoteResult
where	s_name = #{s_name};
